definition.script:
def spaceName = "%SPACE%"
pipeline {
   agent any

   stages {
        stage('Build') {
            steps {
                echo 'Building ....'
                echo 'Build done'
                echo 'Uploading artifact'
            }
        }
        stage('Integration Tests') {
            steps {                
                script {

                    def newSandboxData = null

                    try {
                        //Start Sandbox
                        //TODO replace latest with current artifact from Build stage 
                        newSandboxData = colony.blueprint(
                            spaceName,                           // space name
                            '[Sample]WordPress Basic Stack (AWS)',               // blueprint name
                            'demo-sandobx-' + env.BUILD_NUMBER,  // sandbox name
                            'PT2H', [:], ['DB_USER': 'root', 'DB_PASS':'12345', 'DB_NAME':'wp_demo'], 30                                 // sandbox duration according to ISO 8601 duration
                            )                                                                                                         // timeout
                            .startSandbox()

                        def endpoint = getEndpoint(newSandboxData)

                        echo 'Running integration tests against endpoint: ' + endpoint

                        // TBD - run tests
                    }
                    catch(Exception e) {
                         if (newSandboxData != null) {
                            colony.endSandbox(spaceName, newSandboxData.id)
                        }
                        print e.getClass().getName()
                        print e.message
                        throw e
                    }
                    finally {
                        if (newSandboxData != null) {
                            colony.endSandbox(spaceName, newSandboxData.id)
                        }
                    }
                }                
            }
        }
        stage('UI Tests') {
            steps {                
                echo 'Running UI tests....'

                echo 'Done'
            }
        }
        stage('Load Tests') {
            steps {
                echo 'Running load tests....'

                echo 'Done'
            }
        }
        stage('Security Tests') {
            steps {
                echo 'Running security tests....'

                echo 'Done'
            }
        }
        stage('Backwards Compatibility Tests') {
            steps {
                echo 'Running tests....'

                echo 'Done'
            }
        }
   }
}
// get the first application shortcut for the UI app
def getEndpoint(def newSandboxData) {

    def endpoint = ''

    for (app in newSandboxData.applications) {
        if (app.name == 'wordpress') {
            endpoint = app.shortcuts[0]
            break
        }
    }

    if (endpoint == '') {
        throw new Exception('couldnt get application endpoint from sandbox details')
    }

    return endpoint
}
